    private void filtrarTabla() {
        String ext1 = jTextField3.getText();
        this.ext = ext1;
    }
    private void crearTabla() {
        DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
        model.setRowCount(0);
        String ruta = jTextField1.getText();
        File dir = new File(ruta);
        if(dir.exists()){
            File contenido[] = dir.listFiles();
            for (File contenido1 : contenido) {
                if (contenido1.isFile()) {
                    int ext = contenido1.getName().lastIndexOf(".");
                    if (ext == -1) {
                        model.addRow(new Object[]{contenido1.getName(), null, contenido1.length(), "Fichero"});
                    } else {
                        String exte = contenido1.getName().substring(ext);
                        model.addRow(new Object[]{contenido1.getName(), exte, contenido1.length(), "Fichero"});
                    }
                } else {
                    model.addRow(new Object[]{contenido1.getName(), null, null, "Directorio"});
                }
            }
            try{
                TableRowSorter<TableModel> ordenar = new TableRowSorter<>(model);
                jTable1.setRowSorter(ordenar);
                ordenar.setRowFilter(RowFilter.regexFilter(this.ext, 1));                
                
            }
            catch(NullPointerException ex){
                System.out.println("");
            }
            
        }
    }
